import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
    Card,
    CardHeader,
    ListGroup,
    ListGroupItem,
    Row,
    Col,
    Form,
    FormGroup,
    FormInput,
    FormSelect,
    FormTextarea,
    DatePicker,
    Button
} from "shards-react";
import { useLocation } from 'react-router-dom';

const AssignForm = () => {

    const location = useLocation();

    const [orderAssign, setOrderAssign] = useState({
        title: location.state.rowIndex[0].title,
        assigned_to: "",
        priority: "",
        assign_date: "",
        notes: "",
    });
    const onChangeValue = (e) => {
        setOrderAssign({
            ...orderAssign,
            [e.target.name]: e.target.value
        });
    }

    const submitAssignedOrder = async (event) => {
        try {
            event.preventDefault();
            event.persist();
            axios.post(`http://localhost:8000/apicrud/editusers.php`, {
                ordertitle: orderAssign.title,
                assigned_to: orderAssign.assigned_to,
                priority: orderAssign.priority,
                assign_date: orderAssign.assign_date,
                notes: orderAssign.notes,
            })
                .then(res => {
                    console.log(res.data);
                    return;
                })
        } catch (error) { throw error; }
    };

    useEffect(() => {
        window.scrollTo(0, 0);
        allwriters();
    }, []);

    const [iswriter, setwriter] = useState([]);
    const allwriters = async (ids) => {
        try {
            // axios.get(`http://localhost:8000/essay-helpers/api/assignwriter.php`)
            axios.get(`https://graduate-essay-helpers.com/api/assignwriter.php`)
                .then(res => {
                    console.log(res.data.writerslist.writerdata)
                    setwriter(res.data.writerslist.writerdata);
                })
        } catch (error) { throw error; }
    };

    console.log(iswriter)
    console.log(location)

    return (
        <Card small className="mb-4">
            <CardHeader className="border-bottom">
                <h6 className="m-0">Assignment Form</h6>
            </CardHeader>
            <ListGroup flush>
                <ListGroupItem className="p-3">
                    <Row>
                        <Col>
                            <Form className="insertForm">
                                <FormGroup>
                                    <label htmlFor='title'>Title</label>
                                    <FormInput
                                        id="title"
                                        name="title"
                                        placeholder="Task title"
                                        value={location.state.rowIndex[0].title}
                                        style={{ fontWeight: '500', fontSize: '0.9rem', color: 'black' }}
                                    ></FormInput>
                                </FormGroup>
                                <FormInput
                                    id="title"
                                    name="title"
                                    placeholder="Task title"
                                    value={location.state.rowIndex[0].title}
                                    style={{ fontWeight: '500', fontSize: '0.9rem', color: 'black' }}
                                ></FormInput>
                                <FormGroup>
                                    <label htmlFor="assign">Assigned to:</label>
                                    <FormSelect
                                        style={{ fontWeight: '500', color: 'black' }}
                                    // size="md"
                                    >
                                        <option value="first">Select ---</option>
                                        {iswriter.map((writer, index) => (
                                            <>
                                                <option
                                                    key={writer.id}
                                                >{writer.name}</option>
                                            </>
                                        ))}

                                    </FormSelect>
                                </FormGroup>
                                <FormGroup>
                                    <label htmlFor="priority">Task priority:</label>
                                    <FormSelect
                                        style={{ fontWeight: '500', color: 'black' }}
                                    // size="md"
                                    >
                                        {/* <option value="first">Select ---</option> */}
                                        <option value="first">High</option>
                                        <option value="second">Medium</option>
                                        <option value="third">Low</option>
                                    </FormSelect>
                                </FormGroup>
                                <Row>
                                    <Col>
                                        <FormGroup>
                                            <label htmlFor="start_date">Start date: </label>
                                            <DatePicker
                                            ></DatePicker>
                                        </FormGroup>
                                    </Col>
                                    <Col>
                                        <FormGroup>
                                            <label htmlFor="due_date">Due date: </label>
                                            <DatePicker
                                            ></DatePicker>
                                        </FormGroup>
                                    </Col>
                                </Row>
                                <Row form>
                                    <Col md="12" className="form-group">
                                        <label htmlFor="notes">Notes</label>
                                        <FormTextarea id="notes" name="notes" rows="4" style={{ fontWeight: '500', fontSize: '1rem', color: 'black' }} />
                                    </Col>
                                </Row>
                                <Button
                                    type="submit" theme="accent"
                                    style={{ fontWeight: '500', fontSize: '0.9rem' }}
                                >Save</Button>
                            </Form>
                        </Col>
                    </Row>
                </ListGroupItem>
            </ListGroup>
        </Card>
    )
}

export default AssignForm
