import React, { useState, useEffect } from 'react'
import { Container, Row, Col, Card, CardHeader, CardBody } from "shards-react";

import PageTitle from "../components/common/PageTitle";
import axios from "axios";
import { Link } from "react-router-dom";

import "./ViewUsers.scss";


const ViewUsers = () => {

    const [isuser, setuser] = useState([]);
    const alluser = async (ids) => {
        try {
            axios.get(`http://localhost:8000/essay-helpers/api/getusers.php`)
                .then(res => {
                    console.log(res.data.userlist.userdata)
                    setuser(res.data.userlist.userdata);
                })
        } catch (error) { throw error; }
    };

    return (
        <Container fluid className="main-content-container px-4">
            <h3>User Management</h3>
            <Row noGutters className="page-header py-4">
                <PageTitle title="User List" subtitle="" className="text-sm-left mb-3" />
            </Row>
            <div className="py-4">
                <table class="table border shadow">
                    <thead class="thead-primary">
                        <tr>
                            <th scope="col">Id</th>
                            <th scope="col">Name</th>
                            <th scope="col">Email</th>
                            {/* <th scope="col">Username</th> */}
                            {/* <th scope="col">Phone</th> */}
                            {/* <th scope="col">Type</th> */}
                            {/* <th scope="col">Status</th> */}
                            <th scope="col">Is Email verified</th>
                            <th>date</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <th>hello</th>
                            <td>norbert</td>
                            <td>norbert@gmail.com</td>
                            <td>0712345678</td>
                            <td>norbert</td>
                        </tr>
                        {/* {users.map((user, index) => (
                            <tr>
                                <th scope="row">{index + 1}</th>
                                <td>norbert</td>
                                <td></td>
                                <td></td>
                                <td>

                                </td>
                            </tr>
                        ))} */}
                    </tbody>
                </table>
            </div>
        </Container>
    )
}

export default ViewUsers
