import { useRef, useEffect, useContext } from "react";

import classes from "./LoginForm.module.scss";
import usernameIcon from "../images/akar-icons_person.svg";
import passwordIcon from "../images/carbon_password.svg";
// import ValidUserContext from "../authCheck";

let isInitial = true;

function LoginForm() {
    const validUserContext = useContext(ValidUserContext);

    const emailInputRef = useRef();
    const passwordInputRef = useRef();

    useEffect(() => {
        if (isInitial) {
            validUserContext.localAuthCheck();
            isInitial = false;
        }
    }, [validUserContext]);

    const submitHandler = (event) => {
        event.preventDefault();

        validUserContext.apiAuthCheck(
            emailInputRef.current.value,
            passwordInputRef.current.value
        );
    };

    return (
        <form onSubmit={submitHandler} className={classes.form}>
            <div>
                <img
                    className={classes.icon}
                    src={usernameIcon}
                    alt="Username icon"
                    htmlFor="user-name"
                ></img>
                <input
                    className={classes.input}
                    type="email"
                    id="user-name"
                    name="user-name"
                    autoComplete="on"
                    placeholder="Username or E-mail"
                    ref={emailInputRef}
                    required={!validUserContext.isLoggedIn}
                ></input>
            </div>

            <div>
                <img
                    className={classes.icon}
                    src={passwordIcon}
                    alt="Password icon"
                    htmlFor="user-password"
                ></img>
                <input
                    className={classes.input}
                    type="password"
                    id="user-password"
                    name="user-password"
                    autoComplete="off"
                    placeholder="Password"
                    ref={passwordInputRef}
                    required={!validUserContext.isLoggedIn}
                ></input>
            </div>
            <button
                className={classes.loginBtn}
                disabled={validUserContext.isLoggedIn}
            >
                {validUserContext.isLoggedIn ? "Already logged in" : "Login"}
            </button>
        </form>
    );
}

export default LoginForm;
