import React, { useEffect, useState } from 'react';
import { withRouter } from 'react-router-dom';


const Details = () => {
    let params = withRouter();

    useEffect(() => {
        window.scrollTo(0, 0);
        edituserlist(params.ids);
    }, []);

    const [isuser, setuser] = useState([]);
    const [isloaduser, setloaduser] = useState(false);
    const edituserlist = async (ids) => {
        try {
            axios.post(`http://localhost:8000/apicrud/getusers.php`, {
                userids: ids,
            })
                .then(res => {
                    console.log(res.data.userlist.userdata)
                    setuser(res.data.userlist.userdata[0]);
                    setloaduser(true);
                })
        } catch (error) { throw error; }
    }

    return (
        <div>Details</div>
    )
}

export default Details
