import React, { useState, useEffect, useRef } from "react";
import axios from 'axios';
import {
  Button as Btn
} from "shards-react";
import "./List.css";
import Data from "../data/Data";
import { motion } from "framer-motion";
import {
  cardAnimation,
  ImageTextTransition,
  cardTransition,
} from "../animation/Animation";

import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';

import { images } from '../constant';

const List = () => {

  useEffect(() => {
    window.scrollTo(0, 0);
    allBlogPosts();
  }, []);

  const blgRef = useRef();

  const [blogPost, setblogPost] = useState([]);
  const [docId, setDocId] = useState([]);
  blgRef.current = blogPost;
  const [del, setDel] = useState({
    blgID: "",
  })



  console.log(blgRef)

  const allBlogPosts = async () => {

    const dtArr = [
      // blgId = "",
    ];

    // let one = "http://localhost:8000/essay-helpers/api/getBlogs.php";
    // let two = "http://localhost:8000/essay-helpers/api/deleteblog.php";

    // let one = "https://graduate-essay-helpers.com/api/getBlogs.php";
    // let two = "https://graduate-essay-helpers.com/api/deleteblog.php";

    // const requestOne = axios.post(one, dtArr);
    // const requestTwo = axios.post(two, dtArr);

    try {
      axios.get(`http://localhost:8000/essay-helpers/api/getBlogs.php`)
        .then(res => {
          console.log(res.data.bloglist.blogdata);
          setblogPost(res.data.bloglist.blogdata);
        })
    }
    catch (error) { throw error; }
  }

  const btnClicked = (itemIndex) => {
    const id = blgRef.current[itemIndex].id;

    setDocId(id);

    axios.post(`http://localhost:8000/essay-helpers/api/deleteblog.php`, { blg_id: id })
      .then(res => {
        console.log("deleted");
      })

    console.log(id);
  }

  // ====================================================================================

  const [open, setOpen] = React.useState(false);

  const handleClickOpen = (itemIndex) => {
    const id = blgRef.current[itemIndex].id;
    setDocId(id);
    setOpen(true);
    console.log(id);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleYes = () => {
    setOpen(false);
    axios.post(`http://localhost:8000/essay-helpers/api/deleteblog.php`, { blg_id: docId })
      .then(res => {
        console.log("deleted");
      })
    alert("deleted!..");
  };

  const handleNo = () => {
    setOpen(false);

    alert("you selected no");
  }

  // ====================================================================================


  return (
    <div className="list">
      {Data.map((watch, index) => {
        return (
          <motion.div
            className="list_card"
            layoutId={watch.image}
            transition={cardTransition}>
            <motion.img
              src={`../images/${watch.image}`}
              alt="aluku"
              variants={cardAnimation}
              initial="hidden"
              animate="show"
              transition={ImageTextTransition}
            />
            <motion.p
              variants={cardAnimation}
              initial="hidden"
              animate="show"
              transition={ImageTextTransition}>
              {watch.name}
            </motion.p>
          </motion.div>
        );
      })}
    </div>
  );
};
export default List;
