import React, { useState } from "react";
import axios from 'axios';
import PropTypes from "prop-types";
import {
  Card,
  CardHeader,
  ListGroup,
  ListGroupItem,
  Row,
  Col,
  Form,
  FormGroup,
  FormInput,
  FormSelect,
  FormTextarea,
  Button
} from "shards-react";

const SettingsFormOne = ({ title }) => {

  const [siteInfo, setsiteInfo] = useState({
    site_name: '',
    phone: '',
    comp_email: '',
    map: '',
    address: '',
    facebook: '',
    twitter: '',
    linkedin: '',
    instagram: '',
    youtube: '',
  });

  const onChangeValue = (e) => {
    setsiteInfo({
      ...siteInfo,
      [e.target.name]: e.target.value
    });
  }

  const submitSetting = async (event) => {
    try {
      event.preventDefault();
      event.persist();

      axios.post(
        `http://localhost:8000/essay-helpers/api/addsetting.php`
        // `https://graduate-essay-helpers.com/api/addsetting.php`
        , {
          site_name: userInfo.site_name,
          phone: iserInfo.phone,
          comp_email: userInfo.comp_email,
          map: userInfo.map,
          address: userInfo.address,
          facebook: userInfo.facebook,
          twitter: userInfo.twitter,
          linkedin: userInfo.linkedin,
          instagram: userInfo.instagram,
          youtube: userInfo.youtube,
        })
        .then(res => {
          console.log(res.data);
          return;
        })
    } catch (error) { throw error; }
  };

  return (
    <Card small className="mb-4">
      <CardHeader className="border-bottom">
        <h6 className="m-0">{title}</h6>
      </CardHeader>
      <ListGroup flush>
        <ListGroupItem className="p-3">
          <Row>
            <Col>
              <Form className="insertForm" onSubmit={submitSetting}>
                <FormGroup>
                  <label htmlFor="stName">Site Name</label>
                  <FormInput
                    id="stName"
                    placeholder="site name"
                    value="GraduateEssayHelpers"
                    onChange={onChangeValue}
                  />
                </FormGroup>
                <FormGroup>
                  <label htmlFor="phone">Phone</label>
                  <FormInput
                    id="phone"
                    // type="number"
                    placeholder="phone"
                    value="+1 (201) 381-1580"
                    onChange={onChangeValue}
                  />
                </FormGroup>
                <FormGroup>
                  <label htmlFor="compEmail">Company Email</label>
                  <FormInput
                    id="compEmail"
                    placeholder="company email address"
                    value=""
                    onChange={onChangeValue}
                  />
                </FormGroup>
                <Row form>
                  <Col md="12" className="form-group">
                    <label htmlFor="map">Map</label>
                    <FormTextarea id="map" rows="4" />
                  </Col>
                </Row>
                <FormGroup>
                  <label htmlFor="feAddress">Address with pincode</label>
                  <FormInput
                    id="feAddress"
                    placeholder="Address"
                    value=""
                    onChange={onChangeValue}
                  />
                </FormGroup>
                <Button type="submit" theme="accent">Update</Button>
              </Form>
            </Col>
          </Row>
        </ListGroupItem>
      </ListGroup>
    </Card>
  );
}

SettingsFormOne.propTypes = {
  /**
   * The component's title.
   */
  title: PropTypes.string
};

SettingsFormOne.defaultProps = {
  title: "Details"
};

export default SettingsFormOne;
