import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import axios from 'axios';
import {
    Card,
    CardHeader,
    ListGroup,
    ListGroupItem,
    Row,
    Col,
    Form,
    FormGroup,
    FormInput,
    FormSelect,
    FormTextarea,
    Button
} from "shards-react";
import result from "autoprefixer/data/prefixes";

const GenCoupon = ({ title }) => {


    useEffect(() => {
        generate();
    }, []);

    const [genCoupon, setgenCoupon] = useState({});
    const [coupCode, setCoupCode] = useState({
        coup_code: "random code",
    });


    const generate = (e) => {

        try {
            // e.preventDefault();
            // e.persist();
            axios.post(`http://localhost:8000/essay-helpers/api/gencoupon.php`,
                // axios.post(`https://graduate-essay-helpers.com/api/updateassigning.php`,
                {
                    cpn_code: coupCode.coup_code,
                })
                .then(res => {
                    console.log(res.data);
                    return;
                })
        } catch (error) { throw error; }


        try {
            axios.get(`http://localhost:8000/essay-helpers/api/gencoupon.php`)
                // axios.get(`https://graduate-essay-helpers.com/api/getorders.php`)
                .then(res => {
                    console.log(res.data);
                    setgenCoupon(res.data.coupondata.coupon_code);
                })
        } catch (error) { throw error; }

    }

    // console.log(genCoupon);


    // function generate(e) {
    //     try {
    //         axios.get(`http://localhost:8000/essay-helpers/api/gencoupon.php`)
    //             // axios.get(`https://graduate-essay-helpers.com/api/getorders.php`)
    //             .then(res => {
    //                 console.log(res.data);
    //                 setgenCoupon(res.data.coupondata.coupon_code);
    //             })
    //     } catch (error) { throw error; }
    // }

    // function generate(length) {
    //     var result = '';
    //     var characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789-';
    //     var charactersLength = characters.length;
    //     for (var i = 0; i < length; i++) {
    //         result += characters.charAt(Math.floor(Math.random() *
    //             charactersLength));
    //     }
    //     return result;
    // }

    // const [count, setCount] = useState(0);

    // function test() {
    //     setCount(1);
    //     console.log(toString(setCount(1)));
    //     console.log('test');
    // }

    return (
        <Card small className="mb-4">
            <CardHeader className="border-bottom">
                <h6 className="m-0">{title}</h6>
            </CardHeader>
            <ListGroup flush>
                <ListGroupItem className="p-3">
                    <Row>
                        <Col>
                            <Form>
                                <FormGroup>
                                    <label htmlFor="stName">Generate coupon</label>
                                    <FormInput
                                        id="stName"
                                        name="gen_coupon"
                                        placeholder="generate coupon"
                                        value={genCoupon}
                                        onChange={() => { }}
                                        style={{ fontWeight: '400', color: 'black' }}
                                    />
                                </FormGroup>
                                <FormInput
                                    name="coup_code"
                                    value={coupCode.coup_code}
                                ></FormInput>
                                <Button
                                    theme="accent"
                                    name="cpn_btn"
                                    //   value={generate(20)}
                                    //    onClick={e => console.log(e.target.value)}
                                    onClick={(e) => generate()}
                                >Generate</Button>
                                <br />
                                <br />
                                <FormGroup>
                                    <label htmlFor="discount">Discount</label>
                                    <FormInput
                                        id="discount"
                                        type="number"
                                        placeholder="discount"
                                        // value=""
                                        onChange={() => { }}
                                    />
                                </FormGroup>

                                <Button theme="accent">Save</Button>
                            </Form>
                        </Col>
                    </Row>
                </ListGroupItem>
            </ListGroup>
        </Card>
    );

}

GenCoupon.propTypes = {
    /**
     * The component's title.
     */
    title: PropTypes.string
};

GenCoupon.defaultProps = {
    title: "Details"
};

export default GenCoupon;
