import React, { useCallback, useState } from 'react';
import cuid from "cuid";
import { Container, Row, Col, Form, Button } from "shards-react";
import { useDropzone } from 'react-dropzone';

import PageTitle from "./../components/common/PageTitle";
import NewPostBtn from '../posts/NewPostBtn';

import Dropzone from './Dropzone';
import ImageGrid from './ImageGrid';

const UploadWork = () => {

    const [files, setFiles] = useState([]);
    const { getRootProps, getInputProps } = useDropzone({
        accept: 'image/*',
        onDrop: acceptedFiles => {
            setFiles(acceptedFiles.map(file => Object.assign(file, {
                preview: URL.createObjectURL(file)
            })));
        }
    });

    const thumbs = files.map(file => (
        <div style={thumb} key={file.name}>
            <div style={thumbInner}>
                <img
                    src={file.preview}
                    style={img}
                />
            </div>
        </div>
    ));

    useEffect(() => {
        // Make sure to revoke the data uris to avoid memory leaks
        files.forEach(file => URL.revokeObjectURL(file.preview));
    }, [files]);


    return (
        <Container fluid className="main-content-container px-4">
            <Row noGutters className="page-header py-4">
                <PageTitle title="Upload Work" subtitle="" className="text-sm-left mb-3" />
            </Row>
            <Row>
                <section className="container">
                    <div {...getRootProps({ className: 'dropzone' })}>
                        <input {...getInputProps()} />
                        <p>Drag 'n' drop some files here, or click to select files</p>
                    </div>
                    <aside style={thumbsContainer}>
                        {thumbs}
                    </aside>
                </section>
            </Row>
            <Row>
                <Col>
                    <Form>
                        <Button theme="accent">Create new post</Button>
                    </Form>
                </Col>
            </Row>
            <Row noGutters className="page-header py-4">
                <PageTitle title="Previous work" subtitle="your files" className="text-sm-left mb-3" />
            </Row>
        </Container>

    )
}

export default UploadWork
