import React, { useState, useEffect } from 'react'
import { Container } from 'shards-react'

import PageTitle from "../components/common/PageTitle";
import axios from "axios";
import { Link } from "react-router-dom";

const Orders = () => {
    const [users, setUser] = useState([]);

    useEffect(() => {
        loadUsers();
    }, []);

    const loadUsers = async () => {
        const result = await axios.get("http://localhost:8000/essay-helpers/api/");
        setUser(result.data.reverse());
    };

    const deleteUser = (productId) => {
        axios.delete('http://localhost/ci_cart_geek/delete-product/delete/' + productId)
            .then((result) => {
                loadUsers();
            })
            .catch(() => {
                alert('Error in the Code');
            });
    };

    return (
        <Container fluid className="main-content-container px-4">
            <h3>My Orders</h3>

            <div className="py-4">
                <h3 class="mb-3 text-center" style={{ display: 'flex', textAlign: 'left' }}>Product Details</h3>
                <table class="table border shadow">
                    <thead class="thead-primary">
                        <tr>
                            <th scope="col">Order No</th>
                            <th scope="col">Topic</th>
                            <th scope="col">Status</th>
                            <th scope="col">Type of paper</th>
                            <th scope="col">Pages (words)</th>
                            <th scope="col">Deadline</th>
                            <th scope="col">Bids</th>
                            <th scope="col">Action</th>
                            <th scope="col">Type</th>
                            <th scope="col">Status</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td>null</td>
                            <td>null</td>
                            <td>null</td>
                            <td>null</td>
                            <td>null</td>
                            <td>null</td>
                            <td>null</td>
                            <td>null</td>
                            <td>null</td>
                            <td>null</td>
                        </tr>
                        {/* {users.map((user, index) => (
                            <tr>
                                <th scope="row">{index + 1}</th>
                                <td>{user.product_name}</td>
                                <td>{user.product_price}</td>
                                <td>{user.product_description}</td>
                                <td>
                                    <Link class=" mr-2" to={`/products/edit/${user.id}`}>
                                        <i class="fa fa-edit" aria-hidden="true"></i>
                                    </Link>
                                    <Link class="" onClick={() => deleteUser(user.id)}>
                                        <i class="fa fa-trash" aria-hidden="true"></i>
                                    </Link>
                                </td>
                            </tr>
                        ))} */}
                    </tbody>
                </table>
            </div>
        </Container>
    );
}

export default Orders
