import React, { useEffect, useState, useMemo, useRef } from 'react';
import { Link } from 'react-router-dom';
import {
    Container as Ctn,
    Row
} from "shards-react";
import {
    Container,
    Card,
    CardImg,
    CardText,
    CardBody,
    CardTitle,
    Button
} from 'reactstrap';
import TableContainer from './TableContainer';
import axios from "axios";
import PageTitle from "../components/common/PageTitle";
import 'bootstrap/dist/css/bootstrap.min.css';
import { SelectColumnFilter } from './filters';

import { useParams } from 'react-router-dom';
// import './Order.css';


const ViewUsers = (props) => {

    const param = useParams();

    console.log(param);

    useEffect(() => {
        window.scrollTo(0, 0);
        alluser();
    }, []);

    console.log(props.match)

    const taskRef = useRef();

    const [isuser, setuser] = useState([]);
    taskRef.current = isuser;
    const alluser = async (ids) => {
        try {
            // axios.get(`http://localhost:8000/essay-helpers/api/getorders.php`)
            axios.get(`https://graduate-essay-helpers.com/api/getorders.php`)
                .then(res => {
                    // console.log(res.data.orderlist.orderdata)
                    setuser(res.data.orderlist.orderdata);
                })
        } catch (error) { throw error; }
    };

    const openTask = (rowIndex) => {
        const id = taskRef.current[rowIndex].id;

        // props.history.push({ pathname: "/assign/", search: "?id=" + id, state: { rowIndex: rowIndex } });
        console.log(id);
    }

    console.log()

    let i = 0;

    let ord_id = isuser;

    console.log(ord_id);

    // ==============================================================


    const columns = useMemo(
        () => [
            {
                Header: 'Order No',
                accessor: 'id',
                disableSortBy: true,
                Filter: SelectColumnFilter,
                filter: 'equals',
            },
            {
                Header: 'User id',
                accessor: 'user_id',
            },
            {
                Header: 'Academic level',
                accessor: 'academic_level',
            },
            {
                Header: 'Deadline',
                accessor: 'deadline',
            },
            {
                Header: 'Coupon code',
                accessor: 'coupon_code',
            },
            {
                Header: 'Total',
                accessor: 'total',
            },
            {
                Header: 'Order status',
                accessor: 'order_status',
            },
            {
                Header: 'Payment status',
                accessor: 'payment_status',
            },
            {
                Header: 'Action',
                // Cell: (props) => {
                //     const rowIdx = props.row.id;
                //     return (
                //         <div>
                //             <Button
                //                 onClick={() => openTask(rowIdx)}
                //             >
                //                 Assign
                //             </Button>
                //         </div>
                //     )
                // },
                Cell: (props) => {
                    const rowIdx = props.row.id;
                    // const ord_id = openTask(rowIdx);
                    console.log(props);
                    return (<div><Link to={`/assign/`}><Button className="btn" theme="success">Assign</Button></Link></div>)
                }
            },
            {
                Header: 'Date created',
                accessor: 'created_at',
            },

        ],
        []
    );

    return (
        <Container fluid className="main-content-container px-4">
            <h3 class="mb-3 text-center" style={{ display: 'flex', textAlign: 'left' }}>My Orders</h3>
            <TableContainer
                columns={columns}
                data={isuser}
            />
            <br />
            {/* <br />
                <h3 class="mb-3 text-center" style={{ display: 'flex', textAlign: 'left' }}>In Progress</h3>
                <TableContainer
                    columns={columns}
                    data={isuser}
                /> */}


        </Container>
    )
}

export default ViewUsers;
