import React, { useEffect, useState, useMemo } from 'react';
import { Container, Row, Col, Form, Button } from "shards-react";
import axios from "axios";
import Table, { AvatarCell, SelectColumnFilter, StatusPill } from '../components/task-component/Table';

import PageTitle from "./../components/common/PageTitle";
import NewPostBtn from '../posts/NewPostBtn';

import FileUpload from '../components/files/FileUpload/FileUpload';
import FileList from '../components/files/FileList';

const UploadWork = () => {

    const [files, setFiles] = useState([])

    const removeFile = (filename) => {
        setFiles(files.filter(file => file.name !== filename))
    }

    return (
        <Container fluid className="main-content-container px-4">
            <Row noGutters className="page-header py-4">
                <PageTitle title="Work uploads" subtitle="" className="text-sm-left mb-3" />
            </Row>
            <Row>
                <Col>
                    <div className="App">
                        <div className="title">Upload file</div>
                        <FileUpload files={files} setFiles={setFiles}
                            removeFile={removeFile} />
                        <FileList files={files} removeFile={removeFile} />
                    </div>
                </Col>
            </Row>
            <Row noGutters className="page-header py-4">
                <PageTitle title="Previous work" subtitle="your files" className="text-sm-left mb-3" />
            </Row>

        </Container>

    )
}

export default UploadWork
