import React, { useState, useEffect, useRef } from "react";
import axios from 'axios';
import Data from "../data/Data";
import "./Grid.css";
import { motion } from "framer-motion";
import {
  cardAnimation,
  ImageTextTransition,
  cardTransition,
} from "../animation/Animation";

import {
  Button,
  Form,
  FormGroup,
  FormInput
} from "shards-react";

import { images } from '../constant';

