        blogPost.map((blog, index) => {
          return (
            <motion.div
              className="grid_card"
              layoutId={blog.id}
              transition={cardTransition}
            >

              <motion.img
                src={images.fileicon}
                alt="blogPost"
                variants={cardAnimation}
                initial="hidden"
                animate="show"
                transition={ImageTextTransition}
              />
              <motion.h6
                variants={cardAnimation}
                initial="hidden"
                animate="show"
                transition={ImageTextTransition}>
                {blog.title}
              </motion.h6>
              <motion.div
                className="btn"
                variants={cardAnimation}
                initial="hidden"
                animate="show"
                transition={ImageTextTransition}
              >
                <Button
                  onClick={(e) => {
                    e.preventDefault();
                    window.location.href = `${blog.file_url}`
                  }}
                >view</Button>
                <Form onSubmit={submitForm}>
                  <FormInput
                    name="blgId"
                    value={blog.id}
                    // onChange={changeValue}
                    hidden
                  >
                  </FormInput>
                </Form>
                <Button
                  onClick={() => btnClicked(index)}
                >delete</Button>
              </motion.div>
