    //   [e.target.name]: e.target.value
    // });
    console.log(del)

  }


  const msg = "";
  const btnPress = (e) => {
    // if (window.confirm('Are you sure you wish to delete this item?'));
    // if (window.confirm("Is it chill?")) {
    //   msg = "you said yes";
    //   console.log(msg);
    // }
    // else {
    //   msg = "you said no";
    //   console.log(msg);
    // }
    // console.log(e);
  }

  // ====================================================================================

  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  // ====================================================================================

  return (
    <>
      <div>
        <Button variant="outlined" onClick={handleClickOpen}>
          Open alert dialog
        </Button>
        <Dialog
          open={open}
          onClose={handleClose}
          aria-labelledby="alert-dialog-title"
          aria-describedby="alert-dialog-description"
        >
          <DialogTitle id="alert-dialog-title">
            {"Use Google's location service?"}
          </DialogTitle>
          <DialogContent>
            <DialogContentText id="alert-dialog-description">
              Let Google help apps determine location. This means sending anonymous
              location data to Google, even when no apps are running.
            </DialogContentText>
          </DialogContent>
          <DialogActions>
            <Button onClick={handleClose}>Disagree</Button>
            <Button onClick={handleClose} autoFocus>
              Agree
            </Button>
          </DialogActions>
        </Dialog>
      </div>
      <Button
        onClick={btnPress}
      >press</Button>
      <h5>{msg}</h5>
      <div className="grid">
        {
          blogPost.map((blog, index) => {
            return (
              <motion.div
                className="grid_card"
                layoutId={blog.id}
                transition={cardTransition}
              >

                <motion.img
                  src={images.fileicon}
                  alt="blogPost"
                  variants={cardAnimation}
                  initial="hidden"
                  animate="show"
                  transition={ImageTextTransition}
                />
                <motion.h6
                  variants={cardAnimation}
                  initial="hidden"
                  animate="show"
                  transition={ImageTextTransition}>
                  {blog.title}
                </motion.h6>
                <motion.div
                  className="btn"
                  variants={cardAnimation}
                  initial="hidden"
                  animate="show"
                  transition={ImageTextTransition}
                >
                  <Button
                    onClick={(e) => {
                      e.preventDefault();
                      window.location.href = `${blog.file_url}`
                    }}
                  >view</Button>
                  <Form onSubmit={submitForm}>
                    <FormInput
                      name="blgId"
                      value={blog.id}
                      // onChange={changeValue}
                      hidden
                    >
                    </FormInput>
                  </Form>
                  <Button
                    onClick={() => btnClicked(index)}
                  >delete</Button>
                </motion.div>
              </motion.div>
            )
          })
        }
      </div>
    </>

    // <div className="grid">
    //   {Data.map((watch, index) => {
    //     return (
    //       <motion.div
    //         className="grid_card"
    //         layoutId={watch.image}
    //         transition={cardTransition}>
