import React, { useState, useEffect } from 'react';
import axios from 'axios';

import { useParams, useLocation } from 'react-router-dom';

const Details = (props) => {

    const location = useLocation();

    useEffect(() => {
        console.log(location.pathname); // result: '/secondpage'
        console.log(location.search); // result: '?query=abc'
        console.log(location.state); // result: 'some_value'
    }, [location]);

    console.log(location);

    const myId = location.search.split("?id=");

    console.log(myId[1]);

    const id = myId[1];


    useEffect(() => {
        window.scrollTo(0, 0);
        orderDetail();
    }, []);


    const [orderData, setOrderData] = useState();
    const orderDetail = async (ids) => {
        try {
            axios.get(`http://localhost:8000/essay-helpers/api/getorders.php`)
                .then(res => {
                    console.log(res.data.orderlist.orderdata);
                    setOrderData(res.data.orderlist.orderdata);
                })
        } catch (error) { throw error; }
    }






    return (
        <>
            <div>Details</div>
            <h3>{id}</h3>
            <h4>{ }</h4>
        </>
    )
}

export default Details
