import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import {
    Card,
    CardHeader,
    ListGroup,
    ListGroupItem,
    Row,
    Col,
    Form,
    FormGroup,
    FormInput,
    FormSelect,
    FormTextarea,
    DatePicker,
    FormCheckbox,
    Button
} from "shards-react";


const SearchbarDropdown = (props) => {
    const { options, onInputChange } = props;
    const ulRef = useRef();
    const inputRef = useRef();
    useEffect(() => {
        inputRef.current.addEventListener('click', (event) => {
            event.stopPropagation();
            ulRef.current.style.display = 'flex';
            onInputChange(event);
        });
        document.addEventListener('click', (event) => {
            ulRef.current.style.display = 'none';
        });
    }, []);
    return (
        <div className="search-bar-dropdown">
            <input
                id="search-bar"
                type="text"
                className="form-control"
                placeholder="Search"
                ref={inputRef}
                onChange={onInputChange}
            />
            <ul id="results" className="list-group" ref={ulRef}>
                {options.map((option, index) => {
                    return (
                        <button
                            type="button"
                            key={index}
                            onClick={(e) => {
                                inputRef.current.value = option;
                            }}
                            className="list-group-item list-group-item-action"
                        >
                            {option}
                        </button>
                    );
                })}
            </ul>
        </div>
    );
};


//   =============================================


const OpenForm = () => {


    useEffect(() => {
        window.scrollTo(0, 0);
        allorders();
    }, []);

    const [isorder, setOrder] = useState([]);
    const allorders = async (ids) => {
        try {
            // axios.get(`http://localhost:8000/essay-helpers/api/getorders.php`)
            axios.get(`https://graduate-essay-helpers.com/api/getorders.php`)
                .then(res => {
                    console.log(res.data.orderlist.orderdata)
                    setOrder(res.data.orderlist.orderdata);
                })
        } catch (error) { throw error; }
    };

    console.log(isorder)

    // ========================================================

    const [selected, setSelected] = useState(-1);

    const value = selected !== -1 && isorder[selected];

    // =============================================================



    return (
        <Card small className="mb-4">
            <CardHeader className="border-bottom">
                <h6 className='m-0'>Send Order</h6>
            </CardHeader>
            <ListGroup flush>
                <ListGroupItem className="p-3">
                    <Form>
                        <FormGroup>
                            <label htmlFor='title'><b>Document Title: </b></label>
                            {/* <Row> */}
                            <Col lg="8">
                                <FormSelect
                                    name="title"
                                    id="title"
                                    style={{ fontWeight: '500', fontSize: '1.2rem', color: 'black' }}
                                    size="lg"
                                    onChange={(e) => {
                                        setSelected(e.target.value);
                                    }}
                                    value={selected}
                                >
                                    <option value="select">Select ---</option>
                                    <option>
                                        <FormInput
                                            placeholder="Search"
                                            size="lg"
                                            value="search bar"
                                        ></FormInput>
                                    </option>
                                    {isorder.map((order, index) => (
                                        <>
                                            <option
                                                key={order.id}
                                                value={index}
                                            >{order.title}</option>
                                        </>
                                    ))}
                                </FormSelect>
                            </Col>
                            {/* </Row> */}
                        </FormGroup>
                        <FormGroup>
                            <Row>
                                <Col lg="5">
                                    {value &&
                                        <>
                                            <label htmlFor='order_id'><b>Order id: </b></label>
                                            <FormInput
                                                name="order_id"
                                                id="order_id"
                                                disabled
                                                value={value.id}
                                                style={{ fontWeight: '500', fontSize: '1rem', color: 'black' }}
                                            ></FormInput>
                                        </>
                                    }
                                </Col>
                                <Col lg="5">
                                    {value &&
                                        <>
                                            <label htmlFor='user_id'><b>User id: </b></label>
                                            <FormInput
                                                name="user_id"
                                                id="user_id"
                                                disabled
                                                value={value.user_id}
                                                style={{ fontWeight: '500', fontSize: '1rem', color: 'black' }}
                                            ></FormInput>
                                        </>
                                    }
                                </Col>
                            </Row>
                        </FormGroup>

                        {/* ================================================== */}




                        {/* ====================================================== */}

                        <Button
                            type="submit" theme="accent" value="send"
                            style={{ fontWeight: '500', fontSize: '0.9rem' }}
                        >Open</Button>
                    </Form>
                </ListGroupItem>
            </ListGroup>
        </Card>
    )
}

export default OpenForm
