import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import {
    Card,
    CardHeader,
    ListGroup,
    ListGroupItem,
    Row,
    Col,
    Form,
    FormGroup,
    FormInput,
    FormSelect,
    FormTextarea,
    DatePicker,
    FormCheckbox,
    Button
} from "shards-react";
import { useLocation } from 'react-router-dom';

const OpenForm = (props) => {

    const location = useLocation();





    return (
        <Card small className="mb-4">
            <CardHeader className="border-bottom">
                <h6 className='m-0'>Send Order</h6>
            </CardHeader>
            <ListGroup flush>
                <ListGroupItem className="p-3">
                    <Form onSubmit={submitForm}>
                        <FormGroup>
                            <label htmlFor='title'><b>Document Title: </b></label>
                            {/* <Row> */}
                            <Col lg="8">
                                <FormSelect
                                    name="title"
                                    id="title"
                                    style={{ fontWeight: '500', fontSize: '1.2rem', color: 'black' }}
                                    size="lg"
                                    onChange={(e) => {
                                        setSelected(e.target.value);
                                        setGetTitle({
                                            ...getTitle,
                                            [e.target.name]: e.target.value
                                        });
                                    }}
                                    value={selected}
                                >
                                    <option value="select">Select ---</option>

                                    {isorder.map((order, index) => (
                                        <>
                                            <option
                                                key={order.id}
                                                value={index}
                                            >{order.title}</option>
                                        </>
                                    ))}
                                </FormSelect>
                            </Col>
                            {/* </Row> */}
                        </FormGroup>
                        <FormGroup>
                            <Row>
                                <Col lg="5">
                                    {value &&
                                        <>
                                            <label htmlFor='order_id'><b>Order id: </b></label>
                                            <FormInput
                                                name="order_id"
                                                id="order_id"
                                                disabled
                                                value={value.id}
                                                onChange={onChangeValue}
                                                style={{ fontWeight: '500', fontSize: '1rem', color: 'black' }}
                                            ></FormInput>
                                        </>
                                    }
                                </Col>
                                <Col lg="5">
                                    {value &&
                                        <>
                                            <label htmlFor='user_id'><b>User id: </b></label>
                                            <FormInput
                                                name="user_id"
                                                id="user_id"
                                                disabled
                                                value={value.user_id}
                                                onChange={onChangeValue}
                                                style={{ fontWeight: '500', fontSize: '1rem', color: 'black' }}
                                            ></FormInput>
                                        </>
                                    }
                                </Col>
                            </Row>
                        </FormGroup>

                        <Button
                            type="submit" theme="accent" value="send"
                            style={{ fontWeight: '500', fontSize: '0.9rem' }}
                        >Open</Button>
                    </Form>
                </ListGroupItem>
            </ListGroup>
        </Card>
    )
}

export default OpenForm
