import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
    Card,
    CardHeader,
    ListGroup,
    ListGroupItem,
    Row,
    Col,
    Form,
    FormGroup,
    FormInput,
    FormSelect,
    FormTextarea,
    DatePicker,
    FormCheckbox,
    Button
} from "shards-react";
// import UploadFiles from './UploadFiles (waste)';
// import FileUpload from './UploadFiles';
import UploadFiles from './UploadFiles';

const SendForm = () => {

    useEffect(() => {
        window.scrollTo(0, 0);
        alluser();
    }, []);

    const [isuser, setuser] = useState([]);
    const alluser = async (ids) => {
        try {
            axios.get(`http://localhost:8000/essay-helpers/api/getusers.php`)
                // axios.get(`https://graduate-essay-helpers.com/api/getusers.php`)
                .then(res => {
                    console.log(res.data.userlist.userdata)
                    setuser(res.data.userlist.userdata);
                })
        } catch (error) { throw error; }
    };

    console.log(isuser);

    // ===========================================

    const [selected, setSelected] = useState(-1);

    const value = selected !== -1 && isuser[selected];

    // ===============================================

    return (
        <Card small className="mb-4">
            <CardHeader className="border-bottom">
                <h6 className='m-0'>Send Order</h6>
            </CardHeader>
            <ListGroup flush>
                <ListGroupItem className="p-3">
                    <Form>
                        <Row>
                            <h5>Document Title:</h5>
                        </Row>
                        <Row>
                            <h5>Order No:</h5>
                        </Row>
                        <FormGroup>
                            <Row>
                                <Col>
                                    <label htmlFor='name'>Name: </label>
                                    <FormSelect
                                        name="name"
                                        id="name"
                                        size="lg"
                                        style={{ fontWeight: '500', fontSize: '1.2rem', color: 'black' }}
                                        onChange={(e) => {
                                            setSelected(e.target.value);
                                        }}
                                        value={selected}
                                    >
                                        <option value="first">Select ---</option>
                                        {isuser.map((user, index) => (
                                            <>
                                                <option
                                                    key={user.id}
                                                    value={index}
                                                >{user.name}</option>
                                            </>
                                        ))}
                                    </FormSelect>
                                </Col>
                                <Col>
                                    {value &&
                                        <>
                                            <label htmlFor='email'>Email: </label>
                                            <FormInput
                                                name="email"
                                                id="email"
                                                value={value.email}
                                                style={{ fontWeight: '500', fontSize: '1rem', color: 'black' }}
                                            >
                                            </FormInput></>
                                    }
                                </Col>
                            </Row>
                        </FormGroup>
                        <FormGroup>
                            <Row>
                                <Col>
                                    {value &&
                                        <>
                                            <label htmlFor='user_id'>User id:</label>
                                            <FormInput
                                                name="user_id"
                                                id="user_id"
                                                style={{ fontWeight: '500', fontSize: '1rem', color: 'black' }}
                                                value={value.id}
                                            ></FormInput></>
                                    }
                                </Col>
                                <Col>
                                    <label htmlFor='email' hidden>Order id</label>
                                    <FormInput
                                        name="email"
                                        id="email"
                                        style={{ fontWeight: '500', fontSize: '1rem', color: 'black' }}
                                        hidden
                                    >
                                    </FormInput>
                                </Col>
                            </Row>
                        </FormGroup>
                        <FormGroup>
                            <Row>
                                <Col>
                                    <label htmlFor='check_upload'>Confirm upload to blog</label>
                                    <FormCheckbox
                                        style={{ fontWeight: '500', fontSize: '1rem', color: 'black' }}
                                    >check the box to upload this document to the site's blog page (click on this text to check or uncheck the box)</FormCheckbox>
                                </Col>
                            </Row>
                        </FormGroup>
                        <FormGroup>
                            <Row>
                                <Col md="12" className="form-group">
                                    <label htmlFor="message">Any Message?</label>
                                    <FormTextarea id="message" name="message" rows="4" style={{ fontWeight: '500', fontSize: '1rem', color: 'black' }} />
                                </Col>
                            </Row>
                        </FormGroup>

                        <FormGroup>
                            <label htmlFor='files'>Upload document: </label>
                            <UploadFiles
                                name='files'
                                id='files'
                            />
                            {/* <FileUpload /> */}
                        </FormGroup>


                        <Button
                            type="submit" theme="accent" value="send"
                            style={{ fontWeight: '500', fontSize: '0.9rem' }}
                        >Send</Button>

                    </Form>
                </ListGroupItem>
            </ListGroup>
        </Card>
    )
}

export default SendForm
