import React, { useEffect, useState, useMemo } from 'react';
import { Container, Row, Col } from "shards-react";
import axios from "axios";
import Table, { AvatarCell, SelectColumnFilter, StatusPill } from '../components/task-component/Table';

import PageTitle from "./../components/common/PageTitle";


const Writers = () => {

    const [data, setData] = useState([]);

    // ===========================================================

    useEffect(() => {
        window.scrollTo(0, 0);
        allwriters();
    }, []);

    const [iswriter, setwriter] = useState([]);
    const allwriters = async (ids) => {
        try {
            axios.get(`http://localhost:8000/essay-helpers/api/getwriters.php`)
                // axios.get(`https://graduate-essay-helpers.com/api/getwriters.php`)
                .then(res => {
                    console.log(res.data.writerslist.writerdata)
                    setwriter(res.data.writerslist.writerdata);
                })
        } catch (error) { throw error; }
    };


    const getData = () => {
        const datta = [
            {
                name: 'Jane Cooper',
                email: 'jane.cooper@example.com',
                title: 'Regional Paradigm Technician',
                department: 'Optimization',
                status: 'Completed',
                role: 'Admin',
                age: 27,
                progress: "",
                start: "",
                due: "",
                notes: "",
                imgUrl: 'https://images.unsplash.com/photo-1494790108377-be9c29b29330?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=facearea&facepad=4&w=256&h=256&q=60',
            },
            {
                name: 'Cody Fisher',
                email: 'cody.fisher@example.com',
                title: 'Product Directives Officer',
                department: 'Intranet',
                status: 'In Progress',
                role: 'Owner',
                age: 43,
                progress: "",
                start: "",
                due: "",
                notes: "",
                imgUrl: 'https://images.unsplash.com/photo-1570295999919-56ceb5ecca61?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=facearea&facepad=4&w=256&h=256&q=60',
            },
            {
                name: 'Esther Howard',
                email: 'esther.howard@example.com',
                title: 'Forward Response Developer',
                department: 'Directives',
                status: 'awaiting review',
                role: 'Member',
                age: 32,
                progress: "",
                start: "",
                due: "",
                notes: "",
                imgUrl: 'https://images.unsplash.com/photo-1520813792240-56fc4a3765a7?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=facearea&facepad=4&w=256&h=256&q=60',
            },
            {
                name: 'Jenny Wilson',
                email: 'jenny.wilson@example.com',
                title: 'Central Security Manager',
                department: 'Program',
                status: 'overdue',
                role: 'Member',
                age: 29,
                progress: "",
                start: "",
                due: "",
                notes: "",
                imgUrl: 'https://images.unsplash.com/photo-1498551172505-8ee7ad69f235?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=facearea&facepad=4&w=256&h=256&q=60',
            },
            {
                name: 'Kristin Watson',
                email: 'kristin.watson@example.com',
                title: 'Lean Implementation Liaison',
                department: 'Mobility',
                status: 'On Hold',
                role: 'Admin',
                age: 36,
                progress: "",
                start: "",
                due: "",
                notes: "",
                imgUrl: 'https://images.unsplash.com/photo-1532417344469-368f9ae6d187?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=facearea&facepad=4&w=256&h=256&q=60',
            },
            {
                name: 'Cameron Williamson',
                email: 'cameron.williamson@example.com',
                title: 'Internal Applications Engineer',
                department: 'Security',
                status: 'awaiting review',
                role: 'Member',
                age: 24,
                progress: "",
                start: "",
                due: "",
                notes: "",
                imgUrl: 'https://images.unsplash.com/photo-1566492031773-4f4e44671857?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=facearea&facepad=4&w=256&h=256&q=60',
            },
        ]
        return [...datta, ...datta, ...datta]
    }


    const columns = React.useMemo(() => [
        {
            Header: "Name",
            accessor: 'name',
            Cell: AvatarCell,
            imgAccessor: "imgUrl",
            emailAccessor: "email",
        },
        {
            Header: "Status",
            accessor: 'status',
            Cell: StatusPill,
        },
        {
            Header: "Qualification",
            accessor: 'qualification',
        },
        {
            Header: "Created at",
            accessor: 'created_at',
            Filter: SelectColumnFilter,  // new
            filter: 'includes',
        },
    ], [])

    // const dattta = React.useMemo(() => getData(), [])

    return (
        <Container fluid className="main-content-container px-4">
            <Row noGutters className="page-header py-4">
                <PageTitle title="Total bids" subtitle="" className="text-sm-left mb-3" />
            </Row>

            <div className="min-h-screen bg-gray-100 text-gray-900">
                <main className="max-w-5xl mx-auto px-4 sm:px-6 lg:px-8 pt-4">
                    {/* <div className="">
                    <h1 className="text-xl font-semibold">React Table + Tailwind CSS = ‚ù§</h1>
                </div> */}
                    <div >
                        <Table columns={columns} data={iswriter} />
                    </div>
                </main>
            </div>


        </Container>
    )
}

export default Writers
