    // let one = "https://graduate-essay-helpers.com/api/getBlogs.php";
    // let two = "https://graduate-essay-helpers.com/api/deleteblog.php";

    // const requestOne = axios.post(one, dtArr);
    // const requestTwo = axios.post(two, dtArr);

    try {
      axios.get(`http://localhost:8000/essay-helpers/api/getBlogs.php`)
        .then(res => {
          console.log(res.data.bloglist.blogdata);
          setblogPost(res.data.bloglist.blogdata);
        })
    }
    catch (error) { throw error; }
  }

  const btnClicked = (itemIndex) => {
    const id = blgRef.current[itemIndex].id;

    setDocId(id);

    axios.post(`http://localhost:8000/essay-helpers/api/deleteblog.php`, { blg_id: id })
      .then(res => {
        console.log("deleted");
      })

    console.log(id);
  }

  // const changeValue = (e) => {
