import React, { useCallback, useState } from 'react';
import cuid from "cuid";
import { Container, Row, Col, Form, Button } from "shards-react";

import PageTitle from "./../components/common/PageTitle";
import NewPostBtn from '../posts/NewPostBtn';

import Dropzone from './Dropzone';
import ImageGrid from './ImageGrid';

const UploadWork = () => {

    const [images, setImages] = useState([]);

    const onDrop = useCallback((acceptedFiles) => {

        acceptedFiles.map((file) => {

            const reader = new FileReader();

            reader.onload = function (e) {

                setImages((prevState) => [

                    ...prevState,

                    { id: cuid(), src: e.target.result },

                ]);

            };

            reader.readAsDataURL(file);

            return file;

        });

    }, []);

    return (
        <Container fluid className="main-content-container px-4">
            <Row noGutters className="page-header py-4">
                <PageTitle title="Work uploads" subtitle="" className="text-sm-left mb-3" />
            </Row>
            <Row>
                <main className="App">

                    {/* <h1 className="text-center">Drag and Drop Test</h1> */}

                    <Dropzone onDrop={onDrop} accept={"image/*"} />

                    <ImageGrid images={images} />

                </main>
            </Row>
            <Row>
                <Col>
                    <Form>
                        <Button theme="accent">Create new post</Button>
                    </Form>
                </Col>
            </Row>
            <Row noGutters className="page-header py-4">
                <PageTitle title="Previous work" subtitle="your files" className="text-sm-left mb-3" />
            </Row>
        </Container>

    )
}

export default UploadWork
